import { ConfigType } from '@nestjs/config';
import { UserWithSecurities } from '@modules/auth/types/user.types';
import { UsersService } from '@modules/users/users.service';
import { IJwtPayload } from '../interfaces/jwt-payload.interface';

@Injectable()
export class JwtRefreshStrategy extends PassportStrategy(
  Strategy,
  'jwt-refresh',
) {
  constructor(
    private readonly usersService: UsersService,
    @Inject(APP_CONFIG.KEY)
    readonly configService: ConfigType<AppConfigType>,
  ) {
    super({
      jwtFromRequest: ExtractJwt.fromExtractors([
        (req: Request) => {
          return req?.cookies?.refreshToken as string;
        },
      ]),
      ignoreExpiration: false,
      secretOrKey: configService.authentication.publicKey,
    });
  }

  async validate(payload: IJwtPayload): Promise<UserWithSecurities | null> {
    const user = await this.usersService.findActiveUserByEmail(payload.email);
    if (!user) {
      throw new UnauthorizedException();
    }
    return user;
  }
}

import { Inject, Injectable, UnauthorizedException } from '@nestjs/common';
import { PassportStrategy } from '@nestjs/passport';
import { ExtractJwt, Strategy } from 'passport-jwt';
import { APP_CONFIG, AppConfigType } from '@core/config/config-loader';
import { ConfigType } from '@nestjs/config';
import { UsersService } from '@modules/users/users.service';

@Injectable()
export class JwtStrategy extends PassportStrategy(Strategy, 'jwt') {
  constructor(
    private readonly usersService: UsersService,
    @Inject(APP_CONFIG.KEY)
    private readonly configService: ConfigType<AppConfigType>,
  ) {
    super({
      jwtFromRequest: ExtractJwt.fromAuthHeaderAsBearerToken(),
      ignoreExpiration: false,
      secretOrKey: configService.authentication.publicKey,
    });
  }

  async validate(payload: any) {
    // Se podria validar el usuario en la base de datos para asegurar de que a√∫n existe
    const user = await this.usersService.findActiveUserByEmail(payload.email);
    if (!user) {
      throw new UnauthorizedException();
    }
    // La informaci√≥n del usuario (payload) se a√±adir√° a req.user
    return user;
  }
}

import {
  Injectable,
  NestInterceptor,
  ExecutionContext,
  CallHandler,
  HttpStatus,
} from '@nestjs/common';
import { Observable, tap, catchError, throwError } from 'rxjs';
import { PinoLogger } from 'nestjs-pino';
import { Request } from 'express';
import { TRACE_ID_HEADER } from '@core/middlewares/trace-id.middleware';
import { formatPayload } from '@core/observability/observability.module';

@Injectable()
export class ObservabilityInterceptor implements NestInterceptor {
  constructor(private readonly logger: PinoLogger) {}

  intercept(context: ExecutionContext, next: CallHandler): Observable<any> {
    const now = Date.now();
    const ctx = context.switchToHttp();
    const req = ctx.getRequest<Request>();
    this.logger.info(
      {
        request: req?.body ? formatPayload(req?.body) : undefined,
      },
      `Incoming | ${req.method} ${req.url} | Trace ID: ${req[TRACE_ID_HEADER]}`,
    );

    return next.handle().pipe(
      tap((responseBody) => {
        const latency = Date.now() - now;
        const context = ctx.getResponse();
        const statusCode = context.statusCode;
        const statusName =
          Object.entries(HttpStatus).find(
            ([key, value]) => value === statusCode,
          )?.[0] ?? 'UNKNOWN';
        if (statusCode >= HttpStatus.OK && statusCode < HttpStatus.AMBIGUOUS) {
          this.logger.info(
            {
              response:
                responseBody != undefined
                  ? formatPayload(responseBody)
                  : undefined,
              latencyMs: latency,
            },
            `Success | ${req.method} ${req.url} - (${statusCode} - ${statusName}) | Trace ID: ${req[TRACE_ID_HEADER]}`,
          );
        } else {
          this.logger.error(
            {
              response:
                responseBody != undefined
                  ? formatPayload(responseBody)
                  : undefined,
              latencyMs: latency,
            },
            `Not OK | ${req.method} ${req.url} - (${statusCode} - ${statusName}) | Trace ID: ${req[TRACE_ID_HEADER]}`,
          );
        }
      }),
      catchError((error) => {
        const latency = Date.now() - now;
        const context = ctx.getResponse();
        const statusCode = context.statusCode;
        const statusName =
          Object.entries(HttpStatus).find(
            ([key, value]) => value === statusCode,
          )?.[0] ?? 'UNKNOWN';
        this.logger.error(
          {
            error: error.message,
            stack: error.stack,
            latencyMs: latency,
          },
          `Error | ${req.method} ${req.url} - (${statusCode} - ${statusName}) | Trace ID: ${req[TRACE_ID_HEADER]}`,
        );
        return throwError(() => error);
      }),
    );
  }
}

import { UserResponseDto } from '@api/users/dtos/response/user.response.dto';
import { UserHelper } from '@common/helpers/user.helper';
import { PrismaDatasource } from '@core/database/prisma.service';
import { CryptoHelper } from '@common/helpers/crypto-helpers';
import { OnboardingUserRequestDto } from '@api/onboarding/dto/request/onboarding-user.request.dto';
import { AuthService } from '@modules/auth/services/auth.service';
import { EmailService } from '@modules/email/services/email.service';
import { ConflictException, Injectable, Logger } from '@nestjs/common';
import { UserStatus } from '@prisma/client';
import { PrismaErrorCodes } from '@common/enum/prisma-error-codes.enum';
import { EmailTypeEnum } from '@modules/email/enum/email-type.enum';

@Injectable()
export class OnboardingService {
  private readonly logger = new Logger(OnboardingService.name);

  constructor(
    private readonly emailService: EmailService,
    private readonly authService: AuthService,
    private readonly prisma: PrismaDatasource,
  ) {}

  /**
   * Onboarding de un nuevo usuario.
   * @param dto Datos del usuario a crear.
   * @returns Usuario creado.
   * @remarks Crea un usuario en estado pendiente de verificaci√≥n y env√≠a un correo de verificaci√≥n.
   * Genera un token temporal para la creaci√≥n de contrase√±a y construye un enlace temporal para verificaci√≥n de usuario.
   * Env√≠a el correo electr√≥nico con el enlace.
   */
  async onboarding(
    dto: OnboardingUserRequestDto,
  ): Promise<UserResponseDto | undefined> {
    try {
      const createdUser = await this.prisma.users.create({
        data: {
          firstName: dto.firstName,
          lastName: dto.lastName,
          email: dto.email,
          phoneNumber: dto.phoneNumber,
          createdBy: dto.email,
          status: UserStatus.PENDING_VERIFICATION,
        },
      });

      await this.prisma.userCredentials.create({
        data: {
          userId: createdUser.id,
          passwordHash: CryptoHelper.hashValue(dto.password),
          isActive: true,
        },
      });

      this.emailService.sendEmailByType(
        dto.email,
        EmailTypeEnum.VERIFICATION,
        createdUser,
      );

      return UserHelper.mapUserToResponse(createdUser);
    } catch (error) {
      if (error.code === PrismaErrorCodes.UniqueConstraintFailed) {
        throw new ConflictException('Usuario ya existe.');
      }
      throw error;
    }
  }
}

import {
  Injectable,
  ConflictException,
  NotFoundException,
} from '@nestjs/common';
import { PrismaDatasource } from '@core/database/prisma.service';
import { AssignPermissionToRoleRequestDto } from '@api/permissions/dtos/request/assign-permission-to-role-dto.request';
import { AssignPermissionToUserRequestDto } from '@api/permissions/dtos/request/assign-permission-to-user.request.dto';
import { PermissionAssignedResponseDto } from '@api/permissions/dtos/response/permission-assignment.response.dto';
import { AuditUserEnum } from '@common/enum/audit-user.enum';
import { PrismaErrorCodes } from '@common/enum/prisma-error-codes.enum';

@Injectable()
export class PermissionsAssignmentService {
  constructor(private readonly prisma: PrismaDatasource) {}

  /**
   * Asigna un permiso a un rol.
   * @param dto El DTO con el ID del rol y el ID del permiso.
   * @param createdBy El usuario que realiza la acci√≥n.
   * @returns El objeto de asignaci√≥n creado.
   */
  async assignPermissionToRole(
    dto: AssignPermissionToRoleRequestDto,
    createdBy: string,
  ): Promise<PermissionAssignedResponseDto | null> {
    try {
      const existingAssignment = await this.prisma.rolePermissions.findUnique({
        where: {
          roleId_permissionId: {
            roleId: dto.roleId,
            permissionId: dto.permissionId,
          },
        },
      });

      if (existingAssignment) {
        throw new ConflictException(
          'Este permiso ya est√° asignado a este rol.',
        );
      }

      return await this.prisma.rolePermissions.create({
        data: {
          roleId: dto.roleId,
          permissionId: dto.permissionId,
          createdBy: createdBy,
        },
      });
    } catch (error) {
      // Manejar la excepci√≥n de Prisma para duplicados
      if (error.code === 'P2002') {
        throw new ConflictException(
          'Este permiso ya est√° asignado a este rol.',
        );
      }
      throw error;
    }
  }

  /**
   * Eliminaci√≥n l√≥gica de la asignaci√≥n de un permiso de un rol.
   * @param roleId El ID del rol.
   * @param permissionId El ID del permiso.
   * @param updatedBy El usuario que realiza la acci√≥n.
   * @returns El objeto de asignaci√≥n eliminado.
   */
  async unassignPermissionFromRole(
    roleId: number,
    permissionId: number,
    updatedBy?: string,
  ): Promise<void> {
    const assignment = await this.prisma.rolePermissions.findUnique({
      where: {
        roleId_permissionId: {
          roleId,
          permissionId,
        },
      },
    });

    if (!assignment) {
      throw new NotFoundException('La asignaci√≥n de permiso no existe.');
    }

    await this.prisma.rolePermissions.update({
      where: {
        roleId_permissionId: {
          roleId,
          permissionId,
        },
      },
      data: {
        isActive: false,
        lastChangedAt: new Date(),
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        changedReason: `Eliminado por ${updatedBy ?? AuditUserEnum.SYSTEM}`,
      },
    });
  }

  /**
   * Asigna un permiso a un usuario.
   * @param dto El DTO con el ID del usuario y el ID del permiso.
   * @param createdBy El usuario que realiza la acci√≥n.
   * @returns El objeto de asignaci√≥n creado.
   */
  async assignPermissionToUser(
    dto: AssignPermissionToUserRequestDto,
    createdBy: string,
  ): Promise<PermissionAssignedResponseDto | null> {
    try {
      const existingAssignment = await this.prisma.userPermissions.findUnique({
        where: {
          userId_permissionId: {
            userId: dto.userId,
            permissionId: dto.permissionId,
          },
        },
      });

      if (existingAssignment) {
        throw new ConflictException(
          'Este permiso ya est√° asignado a este usuario.',
        );
      }

      return await this.prisma.userPermissions.create({
        data: {
          userId: dto.userId,
          permissionId: dto.permissionId,
          createdBy: createdBy ?? AuditUserEnum.SYSTEM,
        },
      });
    } catch (error) {
      if (error.code === PrismaErrorCodes.UniqueConstraintFailed) {
        throw new ConflictException(
          'Este permiso ya est√° asignado a este usuario.',
        );
      }
      throw error;
    }
  }

  /**
   * Eliminaci√≥n l√≥gica de la asignaci√≥n de un permiso de un usuario.
   * @param userId El ID del usuario.
   * @param permissionId El ID del permiso.
   * @param updatedBy El usuario que realiza la acci√≥n.
   * @returns El objeto de asignaci√≥n eliminado.
   */
  async unassignPermissionFromUser(
    userId: number,
    permissionId: number,
    updatedBy?: string,
  ): Promise<void> {
    const assignment = await this.prisma.userPermissions.findUnique({
      where: {
        userId_permissionId: {
          userId,
          permissionId,
        },
      },
    });

    if (!assignment) {
      throw new NotFoundException('La asignaci√≥n de permiso no existe.');
    }

    await this.prisma.userPermissions.update({
      where: {
        userId_permissionId: {
          userId,
          permissionId,
        },
      },
      data: {
        isActive: false,
        lastChangedAt: new Date(),
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        changedReason: `Eliminado por ${updatedBy ?? AuditUserEnum.SYSTEM}`,
      },
    });
  }
}

import { CreatePermissionRequestDto } from '@api/permissions/dtos/request/create-permission.request.dto';
import { UpdatePermissionRequestDto } from '@api/permissions/dtos/request/update-permission.request.dto';
import { PermissionResponseDto } from '@api/permissions/dtos/response/permission.response.dto';
import { AuditUserEnum } from '@common/enum/audit-user.enum';
import { PrismaDatasource } from '@core/database/prisma.service';
import { Injectable, NotFoundException } from '@nestjs/common';

@Injectable()
export class PermissionsService {
  constructor(private readonly prisma: PrismaDatasource) {}

  /**
   * Crea un nuevo permiso.
   * @param dto El DTO con el nombre del permiso.
   * @param createdBy El usuario del request, que env√≠a la petici√≥n de creaci√≥n de permiso.
   * @returns El permiso creado.
   */
  async create(
    dto: CreatePermissionRequestDto,
    createdBy?: string,
  ): Promise<PermissionResponseDto | null> {
    return await this.prisma.permission.create({
      data: {
        name: dto.name,
        code: dto.code,
        createdBy: createdBy ?? AuditUserEnum.SYSTEM,
        createdAt: new Date(),
      },
    });
  }

  /**
   * Actualiza un permisso.
   * @param dto El DTO con datos del permiso a actualizar.
   * @param updatedBy El usuario que realiza la acci√≥n.
   * @returns El permiso actualizado.
   */
  async update(
    dto: UpdatePermissionRequestDto,
    updatedBy: string,
  ): Promise<PermissionResponseDto> {
    return await this.prisma.permission.update({
      where: { id: dto.id },
      data: {
        ...dto,
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        lastChangedAt: new Date(),
      },
    });
  }

  /**
   * Obtiene todos los permisos.
   * @returns Una lista de todos los permisos.
   */
  async findAll(): Promise<PermissionResponseDto[] | null> {
    return await this.prisma.permission.findMany();
  }

  /**
   * Obtiene un permiso por su ID.
   * @param id El ID del permiso.
   * @returns El permiso encontrado.
   * @throws NotFoundException si el permiso no existe.
   */
  async findOne(id: number): Promise<PermissionResponseDto | null> {
    return await this.prisma.permission.findUnique({
      where: { id },
    });
  }

  /**
   * Elimina l√≥gica de un permiso por su ID.
   * @param id El ID del permiso.
   * @param updatedBy El usuario que realiza la acci√≥n.
   * @throws NotFoundException si el permiso no existe.
   */
  async remove(id: number, updatedBy?: string): Promise<void> {
    const permission = await this.prisma.permission.findUnique({
      where: { id },
    });
    if (!permission) {
      throw new NotFoundException(`Permiso con ID ${id} no encontrado.`);
    }
    await this.prisma.permission.update({
      where: { id },
      data: {
        isActive: false,
        lastChangedAt: new Date(),
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        changedReason: `Eliminado por ${updatedBy ?? AuditUserEnum.SYSTEM}`,
      },
    });
  }
}

