@Injectable()
export class RolesService {
  constructor(private readonly prisma: PrismaDatasource) {}

  /**
   * Crea un nuevo rol.
   * @param dto El DTO con el nombre del rol.
   * @param createdBy El usuario que realiza la acci√≥n.
   * @returns El rol creado.
   */
  async create(
    dto: CreateRoleRequestDto,
    createdBy: string,
  ): Promise<RoleResponseDto> {
    return await this.prisma.roles.create({
      data: {
        ...dto,
        createdBy,
      },
    });
  }

  /**
   * Actualiza nuevo rol.
   * @param dto El DTO con el nombre del rol.
   * @param updatedBy El usuario que realiza la acci√≥n.
   * @returns El rol actualizado.
   */
  async update(
    dto: UpdateRoleRequestDto,
    updatedBy: string,
  ): Promise<RoleResponseDto> {
    return await this.prisma.roles.update({
      where: { id: dto.id },
      data: {
        ...dto,
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        lastChangedAt: new Date(),
      },
    });
  }

  /**
   * Obtiene todos los roles.
   * @returns Una lista de todos los roles.
   */
  async findAll(): Promise<RoleResponseDto[]> {
    return await this.prisma.roles.findMany();
  }

  /**
   * Obtiene un rol por su ID.
   * @param id El ID del rol.
   * @returns El rol encontrado.
   * @throws NotFoundException si el rol no existe.
   */
  async findOne(id: number): Promise<RoleResponseDto> {
    const role = await this.prisma.roles.findUnique({
      where: { id },
    });
    if (!role) {
      throw new NotFoundException(`Rol con ID ${id} no encontrado.`);
    }
    return role;
  }

  /**
   * Eliminaci√≥n l√≥gica de un rol por su ID.
   * @param id El ID del rol.
   * @param updatedBy El usuario que realiza la acci√≥n.
   * @throws NotFoundException si el rol no existe.
   */
  async remove(id: number, updatedBy: string): Promise<void> {
    const role = await this.prisma.roles.findUnique({
      where: { id },
    });
    if (!role) {
      throw new NotFoundException(`Rol con ID ${id} no encontrado.`);
    }
    await this.prisma.roles.update({
      where: { id },
      data: {
        isActive: false,
        lastChangedAt: new Date(),
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        changedReason: `Eliminado por ${updatedBy ?? AuditUserEnum.SYSTEM}`,
      },
    });
  }
}

import { Injectable } from '@angular/core';

declare const monaco: any;

@Injectable({
  providedIn: 'root'
})
export class TemplateEditorService {
  private editor: any;
  private onChangeCallback: ((value: string) => void) | null = null;

  initializeEditor(container: HTMLElement) {
    // Initialize Monaco Editor
    this.editor = monaco.editor.create(container, {
      value: '',
      language: 'html',
      theme: 'vs-dark',
      automaticLayout: true,
      minimap: {
        enabled: true
      },
      scrollBeyondLastLine: false,
      fontSize: 14,
      wordWrap: 'on',
      lineNumbers: 'on',
      roundedSelection: false,
      scrollbar: {
        horizontal: 'visible',
        vertical: 'visible'
      },
      overviewRulerLanes: 2,
      quickSuggestions: {
        other: true,
        comments: true,
        strings: true
      },
      parameterHints: {
        enabled: true
      },
      autoClosingBrackets: 'always',
      autoClosingQuotes: 'always',
      suggestOnTriggerCharacters: true,
      acceptSuggestionOnEnter: 'on',
      tabCompletion: 'on',
      wordBasedSuggestions: false
    });

    // Set up change listener
    this.editor.onDidChangeModelContent(() => {
      if (this.onChangeCallback) {
        this.onChangeCallback(this.editor.getValue());
      }
    });

    // Register custom language definitions
    this.registerHandlebarsLanguage();
  }

  setEditorContent(content: string, fileType: string) {
    if (this.editor) {
      const language = this.getLanguageFromFileType(fileType);
      const model = monaco.editor.createModel(content, language);
      this.editor.setModel(model);
    }
  }

  setOnChangeCallback(callback: (value: string) => void) {
    this.onChangeCallback = callback;
  }

  private getLanguageFromFileType(fileType: string): string {
    switch (fileType) {
      case 'hbs':
        return 'handlebars';
      case 'css':
      case 'scss':
        return 'css';
      case 'js':
        return 'javascript';
      case 'ts':
        return 'typescript';
      default:
        return 'html';
    }
  }

  private registerHandlebarsLanguage() {
    // Register Handlebars language for Monaco Editor
    if (monaco.languages.getLanguages().find((lang: any) => lang.id === 'handlebars')) {
      return; // Already registered
    }

    monaco.languages.register({ id: 'handlebars' });

    monaco.languages.setMonarchTokensProvider('handlebars', {
      tokenizer: {
        root: [
          [/\{\{\{/, { token: 'keyword', next: '@handlebars_unescaped' }],
          [/\{\{/, { token: 'keyword', next: '@handlebars' }],
          [/<!DOCTYPE/, 'metatag', '@doctype'],
          [/<!--/, 'comment', '@comment'],
          [/(<)(\w+)/, ['delimiter', { token: 'tag', next: '@tag' }]],
          [/(<\/)(\w+)/, ['delimiter', { token: 'tag', next: '@tag' }]],
          [/</, 'delimiter'],
          [/[^<]+/]
        ],

        handlebars_unescaped: [
          [/\}\}\}/, { token: 'keyword', next: '@pop' }],
          [/[^}]+/, 'variable']
        ],

        handlebars: [
          [/\}\}/, { token: 'keyword', next: '@pop' }],
          [/#if|#unless|#each|#with|\/if|\/unless|\/each|\/with/, 'keyword'],
          [/[a-zA-Z_][\w]*/, 'variable'],
          [/[^}]+/, 'variable']
        ],

        comment: [
          [/-->/, 'comment', '@pop'],
          [/[^-]+/, 'comment'],
          [/./, 'comment']
        ],

        doctype: [
          [/[^>]+/, 'metatag.content'],
          [/>/, 'metatag', '@pop']
        ],

        tag: [
          [/[ \t\r\n]+/, 'white'],
          [/(\w+)(\s*=\s*)("([^"]*)")/, ['attribute.name', 'delimiter', 'attribute.value', 'attribute.value']],
          [/(\w+)(\s*=\s*)('([^']*)')/, ['attribute.name', 'delimiter', 'attribute.value', 'attribute.value']],
          [/\w+/, 'attribute.name'],
          [/>/, 'delimiter', '@pop']
        ]
      }
    });

    monaco.languages.setLanguageConfiguration('handlebars', {
      comments: {
        blockComment: ['<!--', '-->']
      },
      brackets: [
        ['<', '>'],
        ['{{', '}}'],
        ['{{{', '}}}']
      ],
      autoClosingPairs: [
        { open: '<', close: '>' },
        { open: '{{', close: '}}' },
        { open: '{{{', close: '}}}' },
        { open: '"', close: '"' },
        { open: "'", close: "'" }
      ],
      surroundingPairs: [
        { open: '<', close: '>' },
        { open: '{{', close: '}}' },
        { open: '{{{', close: '}}}' },
        { open: '"', close: '"' },
        { open: "'", close: "'" }
      ]
    });
  }

  destroy() {
    if (this.editor) {
      this.editor.dispose();
      this.editor = null;
    }
  }
}

import { Injectable, NestMiddleware } from '@nestjs/common';
import { NextFunction, Request, Response } from 'express';
import { v4 as uuid } from 'uuid';

export const TRACE_ID_HEADER = 'X-Trace-Id';

@Injectable()
export class TraceIdMiddleware implements NestMiddleware {
  use(req: Request, res: Response, next: NextFunction) {
    const traceId = uuid();

    req[TRACE_ID_HEADER] = traceId;
    console.log('seteo de header middleware -> ', traceId);
    res.setHeader(TRACE_ID_HEADER, traceId);
    next();
  }
}

import { AuditUserEnum } from '@common/enum/audit-user.enum';
import { PrismaBaseRepository } from '@core/database/prisma-base.repository';
import { PrismaDatasource } from '@core/database/prisma.service';
import { UserCredentialsWithUser } from '@modules/auth/types/user-credentials.types';
import { UserWithSecurities } from '@modules/auth/types/user.types';
import { Injectable } from '@nestjs/common';
import { UserCredentials, Users, UserStatus } from '@prisma/client';

@Injectable()
export class UsersRepository extends PrismaBaseRepository<Users> {
  constructor(prisma: PrismaDatasource) {
    super(prisma, prisma.users);
  }

  /**
   * Busca un usuario activo por su email.
   * @param email Email a buscar.
   * @returns Usuario activo con el email indicado o null si no existe.
   */
  async findActiveUserByEmail(
    email: string,
  ): Promise<UserWithSecurities | null> {
    return await this.prisma.users.findFirst({
      where: {
        email,
        status: { in: [UserStatus.ACTIVE, UserStatus.PENDING_VERIFICATION] },
      },
      include: {
        roles: true,
        UserPermissions: true,
        credentials: true,
        refreshTokens: true,
      },
    });
  }

  /**
   * Busca las credenciales activas de un usuario por su email.
   * @param email Email a buscar.
   * @returns Usuario con sus credenciales y asociaciones, o null si no existe.
   */
  async findCredentialByEmail(
    email: string,
  ): Promise<UserCredentialsWithUser | null> {
    return await this.prisma.userCredentials.findFirst({
      where: {
        isActive: true,
        user: {
          email,
        },
      },
      include: {
        user: {
          include: {
            roles: true,
            UserPermissions: true,
            credentials: true,
            refreshTokens: true,
          },
        },
      },
    });
  }

  /**
   * Actualiza la contrase√±a de un usuario.
   * @param userId ID del usuario.
   * @param newPasswordHash El hash de la nueva contrase√±a.
   */
  async changePassword(userId: number, newPasswordHash: string): Promise<void> {
    // Desactiva la credencial activa actual
    await this.prisma.userCredentials.updateMany({
      where: { userId: userId, isActive: true },
      data: { isActive: false, expiredAt: new Date() },
    });

    // Crea una nueva credencial activa
    await this.prisma.userCredentials.create({
      data: {
        userId,
        passwordHash: newPasswordHash,
        isActive: true,
      },
    });
  }

  /**
   * Actualiza las credenciales de un usuario.
   * @param userCredentialData Datos de la credencial a actualizar.
   */
  async updateCredential(userCredentialData: UserCredentials): Promise<void> {
    await this.prisma.userCredentials.update({
      where: { id: userCredentialData.id, isActive: true },
      data: {
        attempts: userCredentialData.attempts,
        createdAt: userCredentialData.createdAt,
        expiredAt: userCredentialData.expiredAt,
        isActive: userCredentialData.isActive,
        passwordHash: userCredentialData.passwordHash,
        userId: userCredentialData.userId,
      },
    });
  }

  /**
   * Inactivar un usuario por ID. Soft Delete. Delete l√≥gico.
   * @param id ID del usuario a modificar.
   * @param updatedBy Usuario que realiza la modificaci√≥n.
   * @returns Void - inactiva usuario.
   */
  async inactive(id: number, updatedBy?: string): Promise<void> {
    await this.prisma.users.update({
      where: { id },
      data: {
        status: UserStatus.INACTIVE,
        lastChangedBy: updatedBy ?? AuditUserEnum.SYSTEM,
        lastChangedAt: new Date(),
        changedReason: 'Eliminado por ' + (updatedBy ?? AuditUserEnum.SYSTEM),
      },
    });
  }

  /**
   * Actualiza la fecha de √∫ltimo login de un usuario.
   * @param id ID del usuario a actualizar.
   */
  async updateLastLogin(id: number): Promise<void> {
    await this.prisma.users.update({
      where: { id },
      data: {
        lastLogin: new Date(),
      },
    });
  }

  /**
   * Bloquea un usuario cambiando su estado a BLOCKED.
   * @param id ID del usuario a bloquear.
   */
  async blockUser(id: number): Promise<void> {
    await this.prisma.users.update({
      where: { id },
      data: {
        status: UserStatus.BLOCKED,
        lastChangedAt: new Date(),
        lastChangedBy: AuditUserEnum.SYSTEM,
        changedReason: 'Cantidad de intentos fallidos superado',
      },
    });
  }
}

