import {
  Injectable,
  CanActivate,
  ExecutionContext,
  UnauthorizedException,
  ForbiddenException,
  Logger,
} from '@nestjs/common';
import { PrismaDatasource } from '@core/database/prisma.service';
import { ApiClientCredential } from '@prisma/client';

/**
 * export class BasicAuthGuard implements CanActivate {
 * implementa la autenticaci√≥n HTTP Basic (client_id:client_secret)
 * validando las credenciales contra la base de datos.
 * * * Espera el encabezado: Authorization: Basic <base64(client_id:client_secret)>
 */
@Injectable()
export class BasicAuthGuard implements CanActivate {
  private readonly logger = new Logger(BasicAuthGuard.name);

  // Inyectamos el servicio de base de datos (Prisma)
  constructor(private readonly prisma: PrismaDatasource) {}

  async canActivate(context: ExecutionContext): Promise<boolean> {
    const request = context.switchToHttp().getRequest();
    const authHeader = request.headers['authorization'];

    // 1. Validar el formato de Basic Auth
    if (!authHeader || !authHeader.startsWith('Basic ')) {
      throw new UnauthorizedException('Se requiere Basic Authentication.');
    }

    const encodedCredentials = authHeader.substring(6);
    let decodedString: string;

    try {
      // 2. Decodificar Base64
      decodedString = Buffer.from(encodedCredentials, 'base64').toString(
        'utf8',
      );
    } catch (error) {
      this.logger.error(
        `Error de decodificaci√≥n Base64 en Basic Auth. ${error}`,
      );
      throw new UnauthorizedException('Formato de credenciales inv√°lido.');
    }

    const credentials = decodedString.split(':');
    if (credentials.length !== 2) {
      throw new UnauthorizedException(
        'Formato de credenciales Basic Auth incorrecto (debe ser client_id:client_secret).',
      );
    }

    const [clientId, clientSecret] = credentials;

    // 3. Buscar el cliente por ID
    let apiClientCredential: ApiClientCredential;
    try {
      apiClientCredential = (await this.prisma.apiClientCredential.findUnique({
        where: { clientId: clientId },
      })) as unknown as ApiClientCredential;
    } catch (error) {
      this.logger.error(
        'Error de base de datos al buscar credenciales:',
        error.stack,
      );
      throw new ForbiddenException(
        'Error de servicio al validar credenciales.',
      );
    }

    // 4. Verificar si el cliente existe
    if (!apiClientCredential) {
      throw new UnauthorizedException('Client ID incorrecto.');
    }

    // 5. Verificar si la cuenta est√° activa (por si se revoca el acceso)
    if (!apiClientCredential.isActive) {
      this.logger.warn(`Acceso denegado: Cliente ${clientId} inactivo.`);
      throw new ForbiddenException(
        `El acceso para este cliente ha sido revocado.`,
      );
    }

    // 6. Comparar el secreto
    const isPasswordValid = clientSecret === apiClientCredential.secretKey;

    if (!isPasswordValid) {
      // Usamos el mismo mensaje para no dar pistas sobre si fall√≥ el ID o el secreto
      throw new UnauthorizedException('Secreto incorrecto.');
    }

    // Opcional: Adjuntar el objeto del cliente a la request para usarlo en el controlador
    request.apiClient = {
      id: apiClientCredential.clientId,
      name: apiClientCredential.clientName,
    };

    return true; // Autenticaci√≥n exitosa
  }
}

import { PERMISSIONS_KEY } from '@common/decorators/permissions.decorator';
import {
  Injectable,
  CanActivate,
  ExecutionContext,
  ForbiddenException,
} from '@nestjs/common';
import { Reflector } from '@nestjs/core';

@Injectable()
export class PermissionsGuard implements CanActivate {
  constructor(private reflector: Reflector) {}

  canActivate(context: ExecutionContext): boolean {
    // Obtener los permisos requeridos de los metadatos de la ruta
    const requiredPermissions = this.reflector.get<string[]>(
      PERMISSIONS_KEY,
      context.getHandler(),
    );

    // Si no hay permisos definidos, la ruta es de acceso p√∫blico
    if (!requiredPermissions) {
      return true;
    }

    const { user } = context.switchToHttp().getRequest();

    // El objeto 'user' viene de JwtStrategy y contiene los permisos del usuario
    // Si no hay usuario o no tiene permisos, deniega el acceso
    if (!user || !user.permissions) {
      throw new ForbiddenException(`Usuario no permisos asignados.`);
    }

    // Verificar si el usuario tiene al menos uno de los permisos requeridos
    const hasPermission = requiredPermissions.some((permission) =>
      user.permissions.includes(permission),
    );

    // Si el usuario no tiene los permisos necesarios, lanza una excepci√≥n con un mensaje detallado
    if (!hasPermission) {
      throw new ForbiddenException(
        `Usuario no tiene los permisos necesarios: ${requiredPermissions.toString()}.`,
      );
    }

    return hasPermission;
  }
}

import { UsersService } from '@modules/users/users.service';
import {
  Injectable,
  CanActivate,
  ExecutionContext,
  BadRequestException,
  NotFoundException,
} from '@nestjs/common';
import { Users } from '@prisma/client';

@Injectable()
export class UserByEmailLoaderGuard implements CanActivate {
  constructor(private readonly usersService: UsersService) {}

  async canActivate(context: ExecutionContext): Promise<boolean> {
    const request = context.switchToHttp().getRequest();
    const body = request.body;
    const query = request.query;

    if (!body?.email && !query?.email) {
      throw new BadRequestException(
        'La solicitud debe contener un campo "email" en el cuerpo o en los parametros.',
      );
    }

    const email: string = body?.email ?? query?.email;

    const fullUser: Users | null =
      await this.usersService.findActiveUserByEmail(email);

    if (!fullUser) {
      throw new NotFoundException(
        'Usuario no encontrado o inactivo para el email proporcionado.',
      );
    }

    request.user = fullUser;

    return true;
  }
}

