  })
  email: string;

  @ApiProperty({
    example: 'ACTIVE',
    enum: UserStatus,
    description: 'El estado actual del usuario.',
  })
  status: UserStatus;

  @ApiProperty({
    example: 'John',
    description: 'El primer nombre del usuario.',
  })
  firstName: string;

  @ApiProperty({ example: 'Doe', description: 'El apellido del usuario.' })
  lastName: string;

  @ApiProperty({
    example: '12345678',
    description: 'El n√∫mero de documento del usuario.',
  })
  @IsString()
  @IsOptional()
  documentNumber?: string | null;

  @ApiProperty({
    example: '2024-06-16T10:20:30Z',
    description: 'La fecha y hora del √∫ltimo inicio de sesi√≥n.',
  })
  lastLogin: Date;

  @ApiProperty({
    example: 'admin@example.com',
